function CourseReaderModel(cvm, courseModel) {
    var self = this;

    var unitUris = cvm.course().prop('course').execute("getSortedUnitUris", []);

    this.uri = cvm.uri();
    this.courseNumber = ko.observable(cvm.course().prop('course').prop('number').val());
    this.courseName = ko.observable(cvm.course().prop('course').prop('name').val());
    this.curCVMUnitNumber = cvm.curUnitNumber;
    this.curUnitNumber = ko.observable(this.curCVMUnitNumber());
    this.returnUri = cvm.returnUri;

    this.unit = ko.observable(null);
    this.unitNumber = ko.computed(function() {
        return this.unit() ? ('' + (this.curUnitNumber() + 1)) : '';
    }, this);
    this.unitName = ko.computed(function() {
        return this.unit() ? this.unit().prop('name').val() : 'Loading...';
    }, this);
    this.unitContent = ko.computed(function() {
        return this.unit() ? this.unit().prop('content').val() : 'Loading...';
    }, this);
    this.completePercent = ko.computed(function() {
        return Math.round(100 * (this.curUnitNumber() + 1) / unitUris.length);
    }, this);
    this.completeWidth = ko.computed(function() {
        var percent = this.completePercent();
        return "width: " + percent + "%";
    }, this);
    this.nextEnabled = ko.computed(function() {
        return (this.curUnitNumber() + 1) < unitUris.length;
    }, this);
    this.goBackEnabled = (this.returnUri() ? true : false);
    this.doneEnabled = ko.computed(function() {
        return this.goBackEnabled && ((this.curUnitNumber() + 1) == unitUris.length);
    }, this);
    this.prevEnabled = ko.computed(function() {
        return this.curUnitNumber() > 0;
    }, this);
    this.ejectEnabled = ko.computed(function() {
        return this.curUnitNumber() == 0;
    }, this);
    this.nextClick = function() {
        if (self.nextEnabled()) {
            if (ko.isWritableObservable(self.curCVMUnitNumber)){
                self.curCVMUnitNumber(self.curUnitNumber() + 1);
            }
            self.curUnitNumber(self.curUnitNumber() + 1);
            if (courseModel){
                courseModel.goNext();
            }
            window.scrollTo(0, 0);
        }
    };
    this.gotoClick = function(unitNumber) {
        if (ko.isWritableObservable(self.curCVMUnitNumber)){
            self.curCVMUnitNumber(unitNumber);
        }
        self.curUnitNumber(unitNumber);
        window.scrollTo(0, 0);
    };
    this.startClick = function() {
        if (ko.isWritableObservable(self.curCVMUnitNumber)){
            self.curCVMUnitNumber(0);
        }
        self.curUnitNumber(0);
        if (courseModel){
            courseModel.startover();
        }
        window.scrollTo(0, 0);
    };
    this.goBackClick = function() {
        if (self.returnUri()) {
            $cjs.redirect(self.returnUri());
        }
    }
    this.doneClick = function() {
        if (self.doneEnabled()) {
            self.goBackClick();
        }
    };
    this.prevClick = function() {
        if (self.prevEnabled()) {
            if (ko.isWritableObservable(self.curCVMUnitNumber)){
                self.curCVMUnitNumber(self.curUnitNumber() - 1);
            }
            self.curUnitNumber(self.curUnitNumber() - 1);
            if (courseModel){
                courseModel.goPrevious();
            }
            window.scrollTo(0, 0);
        }
    };

    this.reloadUnit = function() {
        var unitNum = self.curUnitNumber();
        if ((unitNum < 0) || (unitNum >= unitUris.length)) {
            // Go back to 0
            if (ko.isWritableObservable(self.curCVMUnitNumber)){
                self.curCVMUnitNumber(0);
            }
            self.curUnitNumber(0);
        } else {
            // Reload unit
            self.unit($cjs(unitUris[unitNum]));
        }
    }

    this.curUnitNumber.subscribe(function() {
        $cjs.session.save();
        self.reloadUnit();
    });

    self.reloadUnit();
};
