/* CSS for course*/
body {
  font-size: 16px;
}

figcaption {
  font-size: smaller;
  font-weight: bold;
}

blockquote {
  font-family: serif;
}

.recap {
  list-style-type: square;
}

.content {
  border: solid 1px #cccccc;
  border-radius: 5px;
  padding: 10px 10px 20px 10px;
}

.btn {
  text-shadow: none;
}

hr {
    clear: both;
}

.sidenote {
   float: right;
   width: 25em;
   max-width: 30%;
   margin: 1em;
   font-size: small;
}

h4 {
    padding-top: 1em;
}

.sociallinks {
    padding-top: 35px;
}

.sociallinks li{
    display: inline;
    list-style-type: none;
    padding-right: 2px;
}

.cjs-twitter-button {
    position: absolute !important;
}

#courseIndex {
    padding-right: 20px;
}

#courseIndex .courseIndex {
    padding-left: 10px;
    padding-right: 10px;
}

#courseIndex .unitIndex {
    padding-top: 5px;
}

#courseIndex a{
    color: #333;
    text-decoration: none;
}

#courseIndex a:hover{
    color: orange;
}

#courseIndex a:visited{
    color: orange;
}

#courseIndex .currentIndex{
    color: orange;
}
